mapper.select:
		select

		from product where 
		now() BETWEEN product.date_start AND  product.date_end AND online = true

		and city_id=#{city_id} 


			and type = #{type}

		 and tid in

            #{tid}

		and `flag` != -1

select
from product where flag=#{flag} and now() BETWEEN product.date_start AND product.date_end
AND city_id=#{city_id}
AND online = true
AND type = #{type}
and `flag` != -1 order by id desc

mapper.insert:
SELECT LAST_INSERT_ID() AS id
insert into product(
)values ( #{name}, #{tid}, #{hospitalId}, #{cityId}, #{price}, #{marketPrice}, #{pPrice}, #{onlinePay}, #{hospitalPay}, #{type}, #{pNum}, #{dateStart}, #{dateEnd}, #{totalNum}, #{remainNum}, #{viewNum}, #{saleNum}, #{online}, #{img}, #{orderNo}, now(), now() )

mapper.select:
select
from product where 1=1 and hospital_id in
#{hospital.id}
