mapper.insert:

	SELECT LAST_INSERT_ID() AS id

insert into vcode(

)values (
#{code}, #{key}, #{expireAt}, #{resendAt}, 0, now(), now(), 0
)

mapper.update:
update vcode set
vcode=#{code},expire_at=#{expireAt},resend_at=#{resendAt},updated_at=#{nowAt},
retry_time=0,seq_id=seq_id+1 where emkey=#{key}
